"""
https://www.hackerrank.com/challenges/the-minion-game/problem
"""

VOWELS = 'AEIOU'


def minion_game(string):
    string = string.upper()
    consonants_count = 0
    vowels_count = 0
    index = 0
    points = len(string)
    while True:
        iter_points = points - index 
        try:
            substring = string[index:]
            if substring[0] in VOWELS:
                vowels_count += iter_points
            else:
                consonants_count += iter_points
            index += 1
        except:
            break
        
    if consonants_count > vowels_count:
        print("{} {}".format('Stuart', consonants_count))
    elif consonants_count < vowels_count:
        print("{} {}".format('Kevin', vowels_count))
    else:
        print("Draw")
    
    
if __name__ == '__main__':
    minion_game("BANANA")
    minion_game("BAANANAS")
    minion_game("")
